import styled from 'styled-components'

export const FooterContainer = styled.div`
  background-color: white;
  color: #fff;
  padding: 24px 0;
  position: relative;
  width: 100vw;
  align-items: center;
  justify-content: center;
  display: flex;
  bottom: 0;
`

export const ExternalLink = styled.a`
  color: #fff;
  margin-right: 32px;
  font-weight: 600;
`
